import React, { useState } from 'react';

import NavbarVertical from "../components/NavbarVertical";
import ActivatedComponent from '../components/CRUD/ActivatedComponent';

import injectable from './../assets/images/conditioning/injectable.png';
import cp from './../assets/images/conditioning/cp.jpg';
import gel from './../assets/images/conditioning/gel.jpg';
import pommade from './../assets/images/conditioning/pommade.jpg';
import sirop from './../assets/images/conditioning/sirop.jpg';
import solute from './../assets/images/conditioning/solute.jpg';
import suppositoire from './../assets/images/conditioning/suppositoire.jpg';

import deleteSymbol from './../assets/images/delete-svgrepo-com.png';

const exampleData = [
    {
        nom: 'Cp',
        activated: true
    },
    {
        nom: 'Sirop',
        activated: true
    },
    {
        nom: 'Pommade',
        activated: true
    },
    {
        nom: 'Gel',
        activated: true
    },
    {
        nom: 'Suppositoire',
        activated: true
    },
    {
        nom: 'Cp',
        activated: true
    },
    {
        nom: 'Cp',
        activated: true
    },
]


const Conditioning = () => {

    const [isActivated, setActivated] = useState(false);

    const handleActivatedClick = () => {
        setActivated(true);
    };

    const handleFormCancel = () => {
        setActivated(false);

    };

    const handleFormConfirm = (formData) => {
        setActivated(false);

    };



  return (
    <div className="flex">
        <NavbarVertical/>
        <div className="p-4">
            <h1 className="text-3xl mt-24 inter ml-10 mb-4">Liste de conditionnement</h1>
            <table className=" table-auto ml-10 w-full mb-6">
                <thead >
                    <tr className='raleway border-b-2 border-gray-400'>
                        <th className='py-4 pl-6'>Image</th>
                        <th className="px-4 py-7">Nom</th>
                        <th className="px-4 py-7">Statut</th>
                        <th className="py-4 px-7">Action</th>
                    </tr>
                </thead>
                <tbody className='inter'>
                    <tr className='border-b'>
                        <td className='py-4 pl-6'>
                            <img className='w-7 h-7' src={injectable} alt="" />
                        </td>
                        <td className="border px-4 py-2">Cp</td>
                        <td className="border px-4 py-2 flex items-center justify-center">
                            <span className="bg-greenApple p-1 border rounded-md text-white">Activé</span>
                        </td>
                        <td>
                            <button onClick= {handleActivatedClick}> <img className='w-6 h-6' src={deleteSymbol} alt="" /></button> 
                        </td>
                    </tr>
                </tbody>
            </table>    

        </div>
        {isActivated && (
                <ActivatedComponent data={exampleData} onCancel={handleFormCancel} onConfirm={handleFormConfirm}/>
            )}


    </div>
  );
};

export default Conditioning;
