import React from 'react';

const PriceComponent = ({ price }) => {

  const handleValidation = () => {
    // Ajoutez ici votre logique de validation
    // Par exemple, vous pouvez afficher une alerte ou effectuer une action spécifique
    alert('Validation réussie !');
  };

  return (
    <div className="text-center">
      {/* Texte "Prix net" avec style Tailwind CSS pour le soulignement en pointillés gris */}
      <span className="border-b border-dotted border-gray-400">
        Prix net :
      </span>

      {/* Texte du prix en vert avec style Tailwind CSS */}
      <span className="text-greenApple">
        {price}F
      </span>

      {/* Bouton "Valider" avec style Tailwind CSS */}
      <button
        onClick={handleValidation}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mt-2"
      >
        Valider
      </button>
    </div>
  );
};

export default PriceComponent;
