import React, { useState } from 'react';

function UserForm2() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    age: '18',
    phoneNumber: '+221 ',
    isMutual: false,
    mutuelleType: '',
    mutuelle: '',
    carnet: '',
  });

  const [isMutualise, setIsMutualise] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const toggleMutual = () => {
    setIsMutualise(!isMutualise);
  };

  return (
    <div className="container mx-auto mt-10">
      <h1 className="text-2xl font-semibold mb-4">Formulaire</h1>
      <form>
        {/* ... Vos autres champs d'entrée */}
        <div className="mb-4">
          <button
            id="mutualToggle"
            data-dropdown-toggle="dropdownHover"
            data-dropdown-trigger="hover"
            type='button'
            onClick={toggleMutual}
            className={`text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center inline-flex items-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800`}
          >
            {isMutualise ? 'Mutualisé' : 'Non Mutualisé'}
            <svg class="w-2.5 h-2.5 ml-2.5" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 10 6">
          <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="m1 1 4 4 4-4"/>
            </svg>
          </button>
          <label className="block text-gray-700 text-sm font-bold mb-2">Statut Mutualisé</label>
        </div>
        {isMutualise && (
          <div>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="mutuelleType">
                Type de Mutuelle
              </label>
              <select
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                name="mutuelleType"
                id="mutuelleType"
                value={formData.mutuelleType}
                onChange={handleChange}
              >
                <option value="">Sélectionnez un type</option>
                <option value="type1">Type 1</option>
                <option value="type2">Type 2</option>
                {/* ... autres options de type de mutuelle */}
              </select>
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="mutuelle">
                Mutuelle
              </label>
              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                type="text"
                name="mutuelle"
                id="mutuelle"
                value={formData.mutuelle}
                onChange={handleChange}
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="carnet">
                Carnet
              </label>
              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                type="text"
                name="carnet"
                id="carnet"
                value={formData.carnet}
                onChange={handleChange}
              />
            </div>
          </div>
        )}
      </form>
    </div>
  );
}

export default UserForm2;
