import React, { useState } from 'react';

const PriceComponent = () => {
  const [priceService, setPriceService] = useState(6000);
  const [priseEnCharge, setPriseEnCharge] = useState(4500);

  // Calcul du prix net
  const priceNet = priceService - priseEnCharge;

  return (
    <div className="flex flex-col items-center justify-center h-screen">
      <div className="text-center">
        {/* Texte "Prix net" avec style Tailwind CSS pour le soulignement en pointillés gris */}
        <span className="border-b border-dotted border-gray-400">
          Prix net :
        </span>

        {/* Texte du prix net en vert avec style Tailwind CSS */}
        <span className="text-green-500">
          {priceNet}F
        </span>
      </div>

      {/* Champ "Prix service" aligné à gauche */}
      <div className="text-left mt-2">
        Prix du service : {priceService}F
      </div>

      {/* Champ "Prise en charge" aligné à gauche */}
      <div className="text-left">
        Prise en charge : <span className="text-red-500">{priseEnCharge}F</span>
      </div>

      {/* Bouton "Valider" avec style Tailwind CSS */}
      <button
        onClick={handleValidation}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mt-2"
      >
        Valider
      </button>
    </div>
  );
};

export default PriceComponent;
