import React, { useState } from 'react';

function generateQuantiteOptions() {
    const quantiteOptions = [];
    for (let quantite = 0; quantite <= 100; quantite++) {
      quantiteOptions.push(quantite.toString());
    }
    return quantiteOptions;
  }
  

const AddMedication = () => {


    const quantiteOptions = generateQuantiteOptions();    
    
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
          ...formData,
          [name]: value,
        });
      };
    
    
    return (  
        <div>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2 raleway" htmlFor="mutuelleType">
                Conditionnement
              </label>
              <select
                className="shadow appearance-none border rounded-lg w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                name="mutuelleType"
                id="mutuelleType"
                onChange={handleChange}
              >
                <option value="">Sélectionnez un type</option>
                <option value="type1">Type 1</option>
                <option value="type2">Type 2</option>
                {/* ... autres options de type de mutuelle */}
              </select>
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2 raleway" htmlFor="mutuelleType">
                Médicament
              </label>
              <select
                className="shadow appearance-none border rounded-lg w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                name="mutuelleType"
                id="mutuelleType"
                onChange={handleChange}
              >
                <option value="">Sélectionnez un type</option>
                <option value="type1">Type 1</option>
                <option value="type2">Type 2</option>
                {/* ... autres options de type de mutuelle */}
              </select>
            </div>
            <div className="w-20 pr-2">
                <label className="block text-gray-700 text-sm font-bold mb-2 raleway" htmlFor="quantite">
                Quantité
                </label>
                <select
                className="shadow appearance-none border rounded-lg w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                name="quantite"
                id="quantite"
                onChange={handleChange}
            >
                {quantiteOptions.map((option) => (
                <option key={option} value={option}>
                    {option}
                </option>
                ))}
            </select>
            </div>

        </div>
    );
}
 
export default AddMedication;
