import React, { useState } from "react";
import Navbar from "../components/Navbar";
import Stepper from "../components/Stepper2";
import Userform from "../components/Userform";

const AddTicketPage = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [complete, setComplete] = useState(false);
  const [formFilled, setFormFilled] = useState(false); // Nouvel état

  // Fonction de rappel pour indiquer que le formulaire est rempli
  const handleFormFilled = () => {
    if (currentStep === 1) {
      setCurrentStep(2); // Passe à l'étape 2 seulement si l'étape actuelle est la première
    }
    setFormFilled(true); // Marquez le formulaire comme rempli
  };

  return (
    <>
      <Navbar />
      <div className="flex justify-center">
        <Stepper currentStep={currentStep} complete={complete} formFilled={formFilled} />
      </div>
      <Userform onFormFilled={handleFormFilled} />
    </>
  );
};

export default AddTicketPage;
