import React, { useState } from 'react';
import pharmacieSign from './../assets/images/pharmacy-sign.png';

const Navbar = ({ page }) => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div className="bg-gray-100 w-1/10 h-screen text-white py-4 border sticky top-0">
    <div className="text-2xl font-semibold flex items-center justify-center pl-4 mb-8 text-greenApple">
      <img src={pharmacieSign} alt="" className="mr-2" />
    </div>
    <nav>
      <ul >
          <li>
            <Link to="/ticketSale" className={`block py-2 pl-3 pr-4 border text-${page == "ticketSale"? 'greenApple' : 'gray-600'}`} aria-current="page">
                  Vente de tickets
                </Link>

            <a href="#" className="block py-2 pl-3 pr-4 border text-gray-600 hover:bg-gray-100 ">Tableau de bord</a>
          </li>
          <li>
            <a href="#" className="block py-2 pl-3 pr-4 border text-gray-600 hover:bg-gray-100 ">Historique</a>
          </li>
          <li>
            <a href="#" className="block py-2 pl-3 pr-4 border text-gray-600 hover:bg-gray-100 ">Conditionnements</a>
          </li>
          <li>
            <a href="#" className="block py-2 pl-3 pr-4 border text-gray-600 hover:bg-gray-100 ">MÃ©dicaments</a>
          </li>
          <li>
            <a href="#" className="block py-2 pl-3 pr-4 border text-gray-600 hover:bg-gray-100 ">Inventaire</a>
          </li>
          <li>
            <a href="#" className="block py-2 pl-3 pr-4 border text-gray-600 hover:bg-gray-100 ">Mutuelles</a>
          </li>
          <li>
            <a href="#" className="block py-2 pl-3 pr-4 border text-gray-600 hover:bg-gray-100 ">Services</a>
          </li>
        </ul>        
      </nav>
    </div>
  );
};

export default Navbar;
