import React, { useState } from 'react';

const AddForm = ({ fields, onCancel, onConfirm }) => {
    const [formData, setFormData] = useState(getInitialFormData(fields));
  
    const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData((prevData) => ({ ...prevData, [name]: value }));
    };
  
    const handleConfirm = () => {
      onConfirm(formData);
    };
  
    return (
      <div className='flex flex-col m-12'>
        {fields.map((field) => (
          <input
            key={field.name}
            type={field.type.toLowerCase()}
            name={field.name}
            value={formData[field.name]}
            onChange={handleChange}
            placeholder={field.placeholder}
            className='border-b my-3 py-1 text-lg'
          />
        ))}
        <div className="flex justify-center mt-4">
          <button
            className="bg-greenApple text-white px-4 py-2 mx-2 rounded"
            onClick={handleConfirm}
          >
            Confirmer
          </button>
          <button
            className="bg-red-700 text-white px-4 py-2 mx-2 rounded"
            onClick={onCancel}
          >
            Annuler
          </button>
        </div>
      </div>
    );
};

const getInitialFormData = (fields) => {
  const initialData = {};
  fields.forEach((field) => {
    initialData[field.name] = '';
  });
  return initialData;
};

export default AddForm;
