const DetailComponent = ({fields, data, onCancel }) => {
  return (
    <div className="fixed top-0 left-0 w-full h-full flex items-center justify-center z-50">
      <div className="fixed top-0 left-0 w-full h-full bg-gray-800 opacity-50 blur"></div>
      <div className="relative bg-white rounded-lg p-4 z-10">
        <div className='flex flex-col m-12'>
        {fields
        .map((field) => (
          <div key={field.name} className="mb-3">

            <span className="font-semibold">{key} : </span> 
                        {key === 'activated' ? (
                          data[key] ? 'Activé' : 'Désactivé'
                        ) : (
                          data[key]
                        )}


            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor={field.name}>
              {field.placeholder}
            </label>
            <input
              type={field.type === 'Long' || field.type === 'Double' ? 'number' : 'text'}
              id={field.name}
              name={field.name}
              value={formData[field.name]}
              onChange={(e) => handleChange(e, field.name)}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            />
          </div>
        ))}

        {Object.keys(data).map((key) => (
          <div key={key} className="mb-3">
            <span className="font-semibold">{key} : </span> 
            {key === 'activated' ? (
              data[key] ? 'Activé' : 'Désactivé'
            ) : (
              data[key]
            )}
          </div>
        ))}          
          <div className="flex justify-center mt-4">
            <button
              className="bg-red-700 hover:bg-red-900 text-white px-4 py-2 mx-2 rounded"
              onClick={onCancel}
            >
              Fermer
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DetailComponent;
