import React, { useState } from "react";
import Navbar from "../components/Navbar";
import Stepper from "../components/Stepper";
import Userform from "../components/Userform";
import AddTicket from "../components/AddTicket"

const AddTicketPage = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [complete, setComplete] = useState(false);

  // Fonction de rappel pour indiquer que le formulaire est rempli
  const handleFormFilled = () => {
    if (currentStep === 1) {
      setCurrentStep(2); // Passe à l'étape 2 seulement si l'étape actuelle est la première
    }
  };

  return (
    <>
      <div className="h-1/5">
        <Navbar/>
      </div>
      <div className="flex justify-center">
        <Stepper currentStep={currentStep} complete={complete}  />
      </div>
      <div className="flex items-center">
      <div className="w-1/2">
        <Userform onFormFilled={handleFormFilled} />
      </div>
      <div className="border-long border-r border-gray-300 h-80 mx-4"></div>
      <AddTicket />
      </div>
    </>
  );
};

export default AddTicketPage;
