import React, { useState } from "react";
import { TiTick } from "react-icons/ti"; // Assurez-vous d'importer TiTick depuis react-icons/ti

const Stepper = ({ currentStep, complete }) => {
  const steps = ["Customer Info", "Shipping Info", "Payment", "Step 4"];

  return (
    <>
      <div className="flex justify-center">
        {steps?.map((step, i) => (
          <div
            key={i}
            className={`step-item ${currentStep === i + 1 && "active"} ${
              (i + 1 < currentStep || complete) && "complete"
            } `}
          >
            <div className="step">
              {i + 1 < currentStep || complete ? <TiTick size={24} /> : i + 1}
            </div>
            <p className="text-gray-500">{step}</p>
          </div>
        ))}
      </div>
      {/* Le reste du contenu du composant Stepper reste inchang√© */}
    </>
  );
};

export default Stepper;
