import React, { useState } from 'react';
import EditComponent from './EditComponent'; // Assurez-vous d'importer le composant EditComponent

const DetailComponent = ({fields, data, onCancel, onConfirm}) => {
  // État pour suivre si le composant d'édition doit être ouvert
  const [isEditOpen, setEditOpen] = useState(false);

  // Gestionnaire pour ouvrir le composant d'édition
  const handleEditClick = () => {
    setEditOpen(true);
    onCancel
  };

  // Gestionnaire pour fermer le composant d'édition
  const handleEditCancel = () => {
    setEditOpen(false);
  };

  return (
    <div className="fixed top-0 left-0 w-full h-full flex items-center justify-center z-50">
      {/* ... Arrière-plan gris et flou ... */}
      <div className="fixed top-0 left-0 w-full h-full bg-gray-800 opacity-50 blur"></div>
      <div className="relative bg-white rounded-lg p-4 z-10">
        <div className='flex flex-col m-12'>
          {Object.keys(data).map((key) => (
            <div key={key} className="mb-3">
              <span className="font-semibold">{key} : </span> {data[key]}
            </div>
          ))}
          <div className="flex justify-center mt-4">
            {/* Bouton pour ouvrir le composant d'édition */}
            <button
              className="bg-yellow-500 text-white px-4 py-2 mx-2 rounded"
              onClick={handleEditClick}
            >
              Modifier
            </button>
            <button
              className="bg-red-700 text-white px-4 py-2 mx-2 rounded"
              onClick={onCancel}
            >
              Fermer
            </button>
          </div>
        </div>
      </div>

      {/* Composant d'édition */}
      {isEditOpen && (
        <EditComponent
          fields={fields}
          data={data} // Passer les données actuelles
          onCancel={handleEditCancel} // Passer la fonction de fermeture
          onConfirm={(formData) => {
            onConfirm(formData)
            setEditOpen(false);
          }}
        />
      )}
    </div>
  );
};

export default DetailComponent;
